import { Router } from "express";
import { authenticationController } from "../../controller/index.js";
import { isLoggedIn, authorize } from "../../utils/middleware.js";
const apiKeyRouter = Router();
apiKeyRouter.get("/generate-key/:id", [isLoggedIn], authorize("Setting"), authenticationController.createAPIKey);
apiKeyRouter.get("/generate-api-key/:id", [isLoggedIn], authorize("Setting"), authenticationController.createDecryptionKey);
apiKeyRouter.get("/get-key/:id", [isLoggedIn], authorize("Setting"), authenticationController.getAPIKey);
apiKeyRouter.get("/get-api-key/:id", [isLoggedIn], authorize("Setting"), authenticationController.getDecryptionKey);
apiKeyRouter.post("/update-password", [isLoggedIn], authorize("Setting"), authenticationController.updatePassword);
export default apiKeyRouter;
